mapper.select:
SELECT userName
FROM member
WHERE userId = #{userId} AND userPw = #{userPw}

SELECT verification
FROM member
WHERE userId = #{userId}

SELECT count(*)
FROM member
WHERE userId = #{userId}

SELECT count(*)
FROM member
WHERE email = #{email}

SELECT count(*)
FROM member
WHERE nickname = #{nickname}

SELECT userPw
FROM member
WHERE userId = #{userId}

mapper.update:
UPDATE member
SET	userName = #{userName},
	nickname = #{nickname},
	email = #{email}
WHERE
	userId = #{userId}

UPDATE member
SET userPw = #{userPw}
WHERE userId = #{userId}

UPDATE member
SET verification = 1
WHERE userId = #{userId}

mapper.select:
SELECT userId
FROM member
WHERE email = #{email}
